import React, {useState} from 'react';
import { StatusBar } from 'expo-status-bar';
import { useFonts } from 'expo-font';
//formik
import {Formik} from 'formik';

//icons

import {Octicons, Ionicons, Fontisto} from '@expo/vector-icons';

import {
    StyledContainer,
    InnerContainer,
    PageLogo,
    PageTitle,
    SubTitle,
    StyledFormArea,
    LeftIcon,
    StyledInputLabel,
    StyledTextInput,
    StyledButton,
    RightIcon,
    Colors,
    ButtonText,
    MsgBox,
    Line,
    ExtraView,
    ExtraText, 
    TextLink,
    TextLinkContent,
    ExtraViewRight
    
} from './../components/styles';

import {Button, View} from 'react-native';

//colors
const {primary, yellow, background, lightgray, darkgray, black } = Colors;
const Signup = () => {
    const [hidePassword, setHidePassword] = useState(true);

    const [agree, setAgree] = useState(false);

    const checkboxHandler = () => {
        setAgree(!agree);
    }
    const btnHandler = () => {
        alert('pog');
    };

    /*
    const [loaded] = useFonts({
        Inter: require('./../assets/fonts/Inter.ttf'),
      });
      */
    return (
        
        <StyledContainer>
            <div className="">
      <div className="container">
        <div>
          <input type="checkbox" id="agree" onChange={checkboxHandler} />
          <label htmlFor="agree"> I agree to <b>terms and conditions</b></label>
        </div>

        {/* Don't miss the exclamation mark* */}
        <button disabled={!agree} className="btn" onClick={btnHandler}>
          Continue
        </button>
      </div>
    </div>
            <StatusBar style = "yellow" />
            <InnerContainer>
                {/* <PageLogo resizeMode = 'contain' source={require('./../assets/login.png')} />
                */}
                <PageTitle>Sign Up</PageTitle>
                
                <SubTitle></SubTitle>
                
                <Formik
                    initialValues={{email: '', password: ''}}
                    onSubmit={(values) => {
                        console.log(values);
                    }}

                >
                    {({handleChange, handleBlur, handleSubmit, values}) => (
                    <StyledFormArea>
                        <MyTextInput
                            label = ""
                            icon=""
                            placeholder ="Name"
                            placeholderTextColor={darkgray}
                            onChangeText={handleChange('email')}
                            onBlur={handleBlur('email')}
                            value={values.email}
                            keyboardType="email-address"
                        />
                        
                        <MyTextInput
                            label = ""
                            icon=""
                            placeholder ="Institution Email"
                            placeholderTextColor={darkgray}
                            onChangeText={handleChange('email')}
                            onBlur={handleBlur('email')}
                            value={values.email}
                            keyboardType="email-address"
                        />

                        <MyTextInput
                            label = ""
                            icon="lock"
                            placeholder ="Password"
                            placeholderTextColor={darkgray}
                            onChangeText={handleChange('password')}
                            onBlur={handleBlur('password')}
                            value={values.password}
                            secureTextEntry={hidePassword}
                            isPassword={true}
                            hidePassword={hidePassword}
                            setHidePassword={setHidePassword}
                        />

                        <MsgBox>...</MsgBox>
                        <StyledButton onPress={handleSubmit}>
                            <ButtonText>
                                Sign Up
                            </ButtonText>
                        </StyledButton>
                        <Line />
                        
                        <ExtraView>
                            <ExtraText></ExtraText>
                            <TextLink>
                                <TextLinkContent>Forgot Your Password?</TextLinkContent>
                            </TextLink>
                        </ExtraView>
                        <ExtraViewRight>
                            <TextLink>
                                <TextLinkContent>Login</TextLinkContent>
                            </TextLink>
                        </ExtraViewRight>
                        
                    </StyledFormArea>
                )}
                </Formik>
            </InnerContainer>
        </StyledContainer>
    );
};

const MyTextInput = ({label, icon, isPassword, hidePassword, setHidePassword, ...props}) => {
    return (
        <View>
            <LeftIcon>
                <Octicons name={icon} size={30} color={yellow} />
            </LeftIcon>
            <StyledInputLabel> {label} </StyledInputLabel>
            <StyledTextInput {...props} />
            {isPassword && (
                <RightIcon onPress={() => setHidePassword(!hidePassword)}>
                    <Ionicons name={hidePassword ? 'md-eye-off' : 'md-eye'} size  = {30} color={darkgray}/>
                </RightIcon>
            )}
        </View>
    )
}

export default Signup;
