import React, {useState} from 'react';
import { StatusBar } from 'expo-status-bar';
import { useFonts } from 'expo-font';
//formik
import {Formik} from 'formik';

//icons

import {Octicons, Ionicons, Fontisto} from '@expo/vector-icons';

import {
    StyledContainer,
    InnerContainer,
    PageLogo,
    PageTitle,
    SubTitle,
    StyledFormArea,
    LeftIcon,
    StyledInputLabel,
    StyledTextInput,
    StyledButton,
    RightIcon,
    Colors,
    ButtonText,
    MsgBox,
    Line,
    ExtraView,
    ExtraText, 
    TextLink,
    TextLinkContent
    
} from './../components/styles';

import {Button, View} from 'react-native';

//colors
const {primary, yellow, background, lightgray, darkgray, black } = Colors;
const Signup = () => {
    const [hidePassword, setHidePassword] = useState(true);
    const [loaded] = useFonts({
        Inter: require('./../assets/fonts/Inter.ttf'),
      });
    return (
        <StyledContainer>
            <StatusBar style = "yellow" />
            <InnerContainer>
                {/* <PageLogo resizeMode = 'contain' source={require('./../assets/login.png')} />
                */}
                <PageTitle>Sign Up</PageTitle>
                <SubTitle></SubTitle>
                
                <Formik
                    initialValues={{email: '', password: ''}}
                    onSubmit={(values) => {
                        console.log(values);
                    }}

                >
                    {({handleChange, handleBlur, handleSubmit, values}) => (
                    <StyledFormArea>
                        <MyTextInput
                            label = "User Name"
                            icon="mail"
                            placeholder ="Name"
                            placeholderTextColor={darkgray}
                            onChangeText={handleChange('email')}
                            onBlur={handleBlur('email')}
                            value={values.email}
                            keyboardType="email-address"
                        />
                        
                        <MyTextInput
                            label = ""
                            icon="mail"
                            placeholder ="Institution Email"
                            placeholderTextColor={darkgray}
                            onChangeText={handleChange('email')}
                            onBlur={handleBlur('email')}
                            value={values.email}
                            keyboardType="email-address"
                        />

                        <MyTextInput
                            label = "Password"
                            icon="lock"
                            placeholder ="Password"
                            placeholderTextColor={darkgray}
                            onChangeText={handleChange('password')}
                            onBlur={handleBlur('password')}
                            value={values.password}
                            secureTextEntry={hidePassword}
                            isPassword={true}
                            hidePassword={hidePassword}
                            setHidePassword={setHidePassword}
                        />

                        <MsgBox>...</MsgBox>
                        <StyledButton onPress={handleSubmit}>
                            <ButtonText>
                                Log In
                            </ButtonText>
                        </StyledButton>
                        <Line />
                        <StyledButton google={true} onPress={handleSubmit}>
                            <Fontisto name="google" color={primary} size={25} />
                            <ButtonText google={true}>
                                Log in with Google
                            </ButtonText>
                        </StyledButton>
                        <ExtraView>
                            <ExtraText></ExtraText>
                            <TextLink>
                                <TextLinkContent>Forgot Your Password?</TextLinkContent>
                            </TextLink>
                        </ExtraView>
                    </StyledFormArea>
                )}
                </Formik>
            </InnerContainer>
        </StyledContainer>
    );
};

const MyTextInput = ({label, icon, isPassword, hidePassword, setHidePassword, ...props}) => {
    return (
        <View>
            <LeftIcon>
                <Octicons name={icon} size={30} color={yellow} />
            </LeftIcon>
            <StyledInputLabel> {label} </StyledInputLabel>
            <StyledTextInput {...props} />
            {isPassword && (
                <RightIcon onPress={() => setHidePassword(!hidePassword)}>
                    <Ionicons name={hidePassword ? 'md-eye-off' : 'md-eye'} size  = {30} color={darkgray}/>
                </RightIcon>
            )}
        </View>
    )
}

export default Signup;
