import React, { useState } from 'react';
import { StatusBar } from 'expo-status-bar';
import { useFonts } from 'expo-font';
//formik
import { Formik } from 'formik';

//icons

import { Octicons, Ionicons, Fontisto } from '@expo/vector-icons';

import {
  StyledContainer,
  InnerContainer,
  PageLogo,
  PageTitle,
  SubTitle,
  StyledFormArea,
  StyledInputLabel,
  StyledTextInput,
  StyledButton,
  RightIcon,
  Colors,
  ButtonText,
  MsgBox,
  Line,
  ExtraView,
  ExtraText,
  TextLink,
  TextLinkContent,
  ExtraViewRight,
} from './../components/styles';

import { Button, View } from 'react-native';

import KeyboardAvoidingWrapper from '../components/KBWrapper';

//colors
const { primary, yellow, background, lightgray, darkgray, black } = Colors;

const Login = ({ navigation }) => {
  const [hidePassword, setHidePassword] = useState(true);
  
  return (
      <StyledContainer>
      {/* //keyboardavoidingwrapper added in, styledcontainer used to be here wrapping the overall thing */}
      {/* <KeyboardAvoidingWrapper> */}
      <StatusBar style="yellow" />
      <InnerContainer>
        {/* removed/commented InnerContainer, attempted to move into KBWrapper */}
        <PageTitle>Log In</PageTitle>

        <Formik
          initialValues={{ email: '', password: '' }}
          onSubmit={(values) => {
            console.log(values);
            navigation.navigate('Signup');
          }}
        >
          {({ handleChange, handleBlur, handleSubmit, values }) => (
            <StyledFormArea>
              <MyTextInput
                label=""
                icon=""
                placeholder="Institution Email"
                placeholderTextColor={darkgray}
                onChangeText={handleChange('email')}
                onBlur={handleBlur('email')}
                value={values.email}
                keyboardType="email-address"
                selectionColor='#FFCC15'
              />

              <MyTextInput
                label=""
                icon=""
                placeholder="Password"
                placeholderTextColor={darkgray}
                onChangeText={handleChange('password')}
                onBlur={handleBlur('password')}
                value={values.password}
                secureTextEntry={hidePassword}
                isPassword={true}
                hidePassword={hidePassword}
                setHidePassword={setHidePassword}
                selectionColor='#FFCC15'
              />
              <MsgBox></MsgBox>
              <StyledButton onPress={handleSubmit}>
                <ButtonText>Log In</ButtonText>
              </StyledButton>
              <StyledButton google={true} onPress={handleSubmit}>
                <Fontisto name="google" color={primary} size={25} />
                <ButtonText google={true}>Log in with Google</ButtonText>
              </StyledButton>
              <ExtraView>
                <TextLink onPress={() => navigation.navigate('Signup')}>
                  <TextLinkContent>Forgot Your Password?</TextLinkContent>
                </TextLink>
              </ExtraView>
            </StyledFormArea>
          )}
        </Formik>
      </InnerContainer>
      {/* </KeyboardAvoidingWrapper> */}
    </StyledContainer>
    
  );
};

const MyTextInput = ({ label, icon, isPassword, hidePassword, setHidePassword, ...props }) => {
  return (
    <View>
      <StyledInputLabel> {label} </StyledInputLabel>
      <StyledTextInput {...props} />
      {isPassword && (
        <RightIcon 
          onPress={() => {
            setHidePassword(!hidePassword);
            }}
        >
          <Ionicons name={hidePassword ? 'md-eye-off' : 'md-eye'} size={30} color={darkgray} />
        </RightIcon>
      )}
    </View>
  );
};

export default Login;
